<?php

namespace App\Tests;

use App\IsSubsequence;
use PHPUnit\Framework\TestCase;

class IsSubsequenceTest extends TestCase
{
    /** @dataProvider dataProvider */
    public function testIsSubsequence(string $s, string $t, bool $expected): void
    {
        $isSubsequence = new IsSubsequence();
        $result = $isSubsequence->isSubsequence($s, $t);

        $this->assertSame($expected, $result);
    }

    public function dataProvider(): array
    {
        return [
            ['bcd', '', true],
            ['abc', 'ahbgdc', true],
            ['axc', 'ahbgdc', false]
        ];
    }
}
